import './App.css';
import DiaryEditor from './DiaryEditor';

function App() {
  const [data, setData] = useState([]);

  const dataId = useRef(0);

  const onCreate = (author, content, emotion) => {
    const newItem = {
      author,
      content,
      emotion,
      id: dataId.current
    }
    dataId.current += 1;
    setData([newItem, ...data]);
  }

  return (
    <div className="App">
      <DiaryEditor />
    </div>
  );
}

export default App;
