const DiaryItem = ({author, content, created_date, emotion, id}) => {
    return (
        <div className="DiaryItem">
            <div className="info">
                <span className="author_info">
                    작성자: {author} | 감정점수 : {emotion}
                </span>
                <br/>
                <span className="date">
                    날짜 : {created_date}
                </span>
            </div>
            <div className="content">
                <span>{content}</span>
            </div>
            <div>
                <button>수정하기</button>
                <button>삭제하기</button>
            </div>
        </div>
    )
}

export default DiaryItem;
